import React, { useRef } from "react";
import CatPerson from "./CatPerson";
import { CSSTransition, TransitionGroup } from "react-transition-group";

const CatItems = ({ data, loading }) => {
  const isEmpty = Object.keys(data).length === 0;
  const cardRefs = useRef([]);

  if (isEmpty && !loading) {
    return (
      <div className="cat__no">
        <h2>Кэшбэка на этот месяц нет!</h2>
      </div>
    );
  }

  return (
    <div className="cat__items">
      <TransitionGroup>
        {Object.keys(data).map((person, index) => (
          <CSSTransition
            key={person}
            timeout={300}
            classNames="card"
            nodeRef={cardRefs.current[index]} // Передаем ref
          >
            <CatPerson
              ref={(el) => (cardRefs.current[index] = el)}
              person={person}
              data={data[person]}
            />
          </CSSTransition>
        ))}
      </TransitionGroup>
    </div>
  );
};


export default CatItems;
