import React from "react";
import CatPerson from "./CatPerson";
import { CSSTransition, TransitionGroup } from "react-transition-group";

const CatItems = ({ data, loading }) => {
  const isEmpty = Object.keys(data).length === 0;

  if (isEmpty && !loading) {
    // Отображение надписи только при загруженных данных и пустом объекте данных
    return (
      <div className="cat__no">
        <h2>Кэшбэка на этот месяц нет!</h2>
      </div>
    );
  }

  return (
    <div className="cat__items">
      {Object.keys(data).map((person) => (
        <CatPerson key={person} person={person} data={data[person]} />
      ))}

      <TransitionGroup>
        {Object.keys(data).map((person) => (
          <CSSTransition key={person} timeout={300} classNames="post">
            <CatPerson key={data[person]} person={person} data={data[person]} />
          </CSSTransition>
        ))}
      </TransitionGroup>
    </div>
  );
};

export default CatItems;
